import React from 'react'
import styled from 'styled-components'
import { dayOfWeekStyles, weekHeaderStyles } from './styles'

const StyledWeekHeader = styled.div`${weekHeaderStyles}`
const StyledDayOfWeek = styled.span`${dayOfWeekStyles}`

class WeekHeader extends React.Component {
  render() {

    const {
      children,
      DayOfWeek,
      ...rest
    } = this.props

    return (
      <StyledWeekHeader {...rest}>
        <StyledDayOfWeek><DayOfWeek>Sun</DayOfWeek></StyledDayOfWeek>
        <StyledDayOfWeek><DayOfWeek>Mon</DayOfWeek></StyledDayOfWeek>
        <StyledDayOfWeek><DayOfWeek>Tues</DayOfWeek></StyledDayOfWeek>
        <StyledDayOfWeek><DayOfWeek>Wed</DayOfWeek></StyledDayOfWeek>
        <StyledDayOfWeek><DayOfWeek>Thurs</DayOfWeek></StyledDayOfWeek>
        <StyledDayOfWeek><DayOfWeek>Fri</DayOfWeek></StyledDayOfWeek>
        <StyledDayOfWeek><DayOfWeek>Sat</DayOfWeek></StyledDayOfWeek>
      </StyledWeekHeader>
    )
  }
}
export default WeekHeader
