import { css } from 'styled-components'


const makeColor = ({ disabled, selected }) => {
  if (selected) {
    return 'color: red;'
  }
  if (disabled) {
    return 'color: blue;'
  }
  return 'color: black;'
}

const dayStyles = ({
  ...rest
}) => css`
  float: left;
  width: 14.285714285714286%;
  ${makeColor(rest)}
`
const weekStyles = css`
`

export {
  weekStyles,
  dayStyles,
}
