import React from 'react'
import styled from 'styled-components'
import { dayOfWeekStyles, weekHeaderStyles } from './styles'

const StyledWeekHeader = styled.div`${weekHeaderStyles}`
const StyledDayOfWeek = styled.span`${dayOfWeekStyles}`

class WeekHeader extends React.Component {
  render() {

    const {
      children,
      ...rest
    } = this.props

    return (
      <StyledWeekHeader {...rest}>
        <StyledDayOfWeek>Sun</StyledDayOfWeek>
        <StyledDayOfWeek>Mon</StyledDayOfWeek>
        <StyledDayOfWeek>Tues</StyledDayOfWeek>
        <StyledDayOfWeek>Wed</StyledDayOfWeek>
        <StyledDayOfWeek>Thurs</StyledDayOfWeek>
        <StyledDayOfWeek>Fri</StyledDayOfWeek>
        <StyledDayOfWeek>Sat</StyledDayOfWeek>
      </StyledWeekHeader>
    )
  }
}
export default WeekHeader
